  ('post-uuid-ux-trends-2025', 'ux-design-trends-2025', 'Top 5 UX Design Trends to Watch in 2025', 'Explore the cutting-edge user experience design trends that are set to define digital product interactions in the coming year.', '{"blocks": [{"type": "header", "data": {"text": "Introduction to 2025 UX Trends", "level": 2}}, {"type": "paragraph", "data": {"text": "As technology evolves, so does the landscape of UX design. Here are the key trends..."}}, {"type": "list", "data": {"style": "ordered", "items": ["AI-Driven Personalization", "Hyper-Immersive Experiences (AR/VR)", "Ethical Design & Digital Wellbeing", "Advanced Accessibility", "Voice User Interfaces (VUI) 2.0"]}}]}', '/images/seed/blog/ux-trends-2025.png', '/images/seed/og/blog/ux-trends-2025-og.png', 'published', NOW() - interval '3 days', NULL, TRUE, NOW() - interval '3 days', NOW()),
  ('post-uuid-accessible-design', 'importance-of-accessible-design', 'Why Accessible Design is Non-Negotiable', 'Discover the critical importance of creating accessible digital products for all users and how it benefits businesses.', '{"blocks": [{"type": "header", "data": {"text": "The Case for Accessibility", "level": 2}}, {"type": "paragraph", "data": {"text": "Accessibility is not just a feature, it''s a fundamental right and a business imperative..."}}]}', '/images/seed/blog/accessible-design.png', '/images/seed/og/blog/accessible-design-og.png', 'published', NOW() - interval '10 days', NULL, FALSE, NOW() - interval '10 days', NOW()),
  ('post-uuid-future-of-work', 'future-of-remote-ux-work', 'The Future of Remote UX Collaboration', 'Insights into tools and strategies for effective remote UX teamwork and design sprints.', '{"blocks": [{"type": "paragraph", "data": {"text": "Remote work is here to stay. How can UX teams thrive?..."}}]}', '/images/seed/blog/remote-ux-work.png', '/images/seed/og/blog/remote-ux-work-og.png', 'draft', NULL, NULL, FALSE, NOW() - interval '1 day', NOW());
